function [D] = get_MassInertia(th2, th3)

global lz1 lz2 lz3 L1 L2 L3 g m1 m2 m3 r1 r2 r3 tau1 tau2 tau3;

D = [lz1 + lz2 + lz3 - L1^2*m1 + L1^2*m2 + L1^2*m3 - L2^2*m2 + L2^2*m3 - L3^2*m3 + 2*L1*m1*r1 + 2*L2*m2*r2 + 2*L3*m3*r3 + 2*L1*m3*r3*cos(th2 + th3) + 2*L1*L2*m3*cos(th2) + 2*L1*m2*r2*cos(th2) + 2*L2*m3*r3*cos(th3), lz2 + lz3 - L2^2*m2 + L2^2*m3 - L3^2*m3 + 2*L2*m2*r2 + 2*L3*m3*r3 + L1*m3*r3*cos(th2 + th3) + L1*L2*m3*cos(th2) + L1*m2*r2*cos(th2) + 2*L2*m3*r3*cos(th3), - m3*L3^2 + 2*m3*r3*L3 + lz3 + L1*m3*r3*cos(th2 + th3) + L2*m3*r3*cos(th3);
                                                       lz2 + lz3 - L2^2*m2 + L2^2*m3 - L3^2*m3 + 2*L2*m2*r2 + 2*L3*m3*r3 + L1*m3*r3*cos(th2 + th3) + L1*L2*m3*cos(th2) + L1*m2*r2*cos(th2) + 2*L2*m3*r3*cos(th3),                                                                   lz2 + lz3 - L2^2*m2 + L2^2*m3 - L3^2*m3 + 2*L2*m2*r2 + 2*L3*m3*r3 + 2*L2*m3*r3*cos(th3),                           - m3*L3^2 + 2*m3*r3*L3 + lz3 + L2*m3*r3*cos(th3);
                                                                                                                                      - m3*L3^2 + 2*m3*r3*L3 + lz3 + L1*m3*r3*cos(th2 + th3) + L2*m3*r3*cos(th3),                                                                                                          - m3*L3^2 + 2*m3*r3*L3 + lz3 + L2*m3*r3*cos(th3),                                               - m3*L3^2 + 2*m3*r3*L3 + lz3];